package com.claassen.jlinq;import java.util.Collection;import java.util.Iterator;public class JLinqCollection<T> extends JLinqBase<T> {    public JLinqCollection(Collection<T> collection) {        Iterator<T> iterator = collection.iterator();        setNext(() -> iterator.next());        setHasNext(() -> iterator.hasNext());    }    public static <U> JLinqCollection<U> query(Collection<U> collection) {        return new JLinqCollection<>(collection);    }}