package com.github.claassen.jlinq;import java.util.Iterator;public class JLinqCollection<T> extends JLinqBase<T> {    public JLinqCollection(Iterable<T> collection) {        Iterator<T> iterator = collection.iterator();        setNext(() -> iterator.next());        setHasNext(() -> iterator.hasNext());    }    public static <U> JLinqCollection<U> query(Iterable<U> collection) {        return new JLinqCollection<>(collection);    }}