package com.github.claassen.jlinq.queries;import com.github.claassen.jlinq.queries.base.JLinqBase;import java.util.Iterator;public class JLinqCollection<T> extends JLinqBase<T> {    public JLinqCollection(Iterable<T> collection) {        Iterator<T> iterator = collection.iterator();        setNext(() -> iterator.next());        setHasNext(() -> iterator.hasNext());    }}